create table jaguar_account (account_id bigint not null auto_increment, active boolean default false not null, created TIMESTAMP DEFAULT CURRENT_TIMESTAMP not null, modified TIMESTAMP DEFAULT CURRENT_TIMESTAMP, account_name varchar(255) not null, city varchar(255) not null, country varchar(255) not null, postal_code varchar(255) not null, state varchar(255) not null, primary key (account_id), unique (account_name)) type=InnoDB;
create table jaguar_user (user_id bigint not null auto_increment, active boolean default false not null, created TIMESTAMP DEFAULT CURRENT_TIMESTAMP not null, modified TIMESTAMP DEFAULT CURRENT_TIMESTAMP, email varchar(100) not null, first_name varchar(50), last_name varchar(50), last_online datetime, name varchar(100), password varchar(100) not null, account_id bigint not null, primary key (user_id), unique (account_id, email)) type=InnoDB;
alter table jaguar_user add index fk_user_account_id (account_id), add constraint fk_user_account_id foreign key (account_id) references jaguar_account (account_id);
